import React from 'react';
import ReactDOM from 'react-dom';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import MaterialIcons from 'material-icons-react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';
import Sweetalert from 'react-bootstrap-sweetalert';
import axios from 'axios';
import Header from './Components/Header';
import Footer from './Components/Footer';

class AdminDashboard extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            table_data : [{username : 'monik', password : 'behera'}],
            loginAlertShow : false,
            loginAlertText : ""
        }        
    }

    handleLoginAlertOpen(){
        this.setState({loginAlertShow : true});
    }

    handleLoginAlertClose(){
        this.setState({loginAlertShow : false});
    }

    componentDidMount(){
        this.fetchRow();
    }

    fetchRow(){
        var _self = this;
        axios({
            url : '/admin-moderator-crud',
            method : 'get',
            headers : {
                authtoken:localStorage.getItem('authtoken')
            }
        })
        .then(function(response){
            console.log(response);
            if(response.data.status == 'success'){
                _self.setState({table_data : response.data.list});
            }
            else{
                _self.setState({loginAlertText : response.data.message});
                _self.handleLoginAlertOpen();
            }
        })
        .catch(function(error){
            console.log(error);
        });
    }

    addRow(){
        var _self = this;
        axios({
            url : '/admin-moderator-crud',
            method : 'post',
            headers : {
                authtoken:localStorage.getItem('authtoken')
            },
            data : {
                username : "dummy",
                password : "dummy"
            }
        })
        .then(function(response){
            console.log(response);
            if(response.data.status == 'success'){
                _self.fetchRow();
            }
            else{
                _self.setState({loginAlertText : response.data.message});
                _self.handleLoginAlertOpen();
            }
        })
        .catch(function(error){
            console.log(error);
        });
    }

    removeRow(){
        var _self = this;
        axios({
            url : '/admin-moderator-crud',
            method : 'delete',
            headers : {
                authtoken:localStorage.getItem('authtoken')
            },
            data : {
                username : "dummy",
                password : "dummy"
            }
        })
        .then(function(response){
            console.log(response);
            if(response.data.status == 'success'){
                _self.fetchRow();
            }
            else{
                _self.setState({loginAlertText : response.data.message});
                _self.handleLoginAlertOpen();
            }
        })
        .catch(function(error){
            console.log(error);
        });
    }

    render(){

        const styleTable = {
            paddingTop : '15vh'
        }

        return(
            <Container>
                <Header/>
                <div style={styleTable}>
                    <Table responsive bordered striped variant="light">
                        <tr>
                            <th>#</th>
                            <th>Username</th>
                            <th>Password</th>
                            <th><Button variant="link"><MaterialIcons icon="add_circle" /></Button></th>
                        </tr>
                        {
                            this.state.table_data.map(function(row, index){
                                <tr key={row.username}>
                                    <td>{index}</td>
                                    <td>{row.username}</td>
                                    <td>{row.password}</td>
                                </tr>
                            })
                        }
                    </Table>
                </div>
                <Footer/>
                <Sweetalert danger confirmBtnText="I Understand" confirmBtnBsStyle="danger" title="Data Error" show = {this.state.loginAlertShow} onConfirm = {this.handleLoginAlertClose}>
                    {this.state.loginAlertText}
                </Sweetalert>
            </Container>
        )
    }
}

export default AdminDashboard;
