import React from 'react';
import ReactDOM from 'react-dom';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import MaterialIcons from 'material-icons-react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab'
import Sweetalert from 'react-bootstrap-sweetalert';
import axios from 'axios';
import Header from './Components/Header';
import Footer from './Components/Footer';
import $ from 'jquery';

class ModeratorDashboard extends React.Component{

    constructor(props){
        super(props)
    }

    componentDidMount(){
        $("#gallery-file").on("change", function(e){
            $("#gallery-filename").html(e.currentTarget.files[0].name)
            // console.log(e);
        })
    }    

    render(){

        const styleTableDiv = {
            marginTop : '15vh',
            marginBottom : '15vh',
            background : "#eeeeee"

        }

        return(
            <Container>
                <Header/>
                    <div style={styleTableDiv}>
                        <Tabs defaultActiveKey="gallery">
                            <Tab eventKey="gallery" title="GALLERY">
                                <div style={{padding:"2vw"}}>
                                    {/* <Container> */}
                                        <Row style={{textAlign:"center",}}>
                                            <Form>
                                                <Form.Row>
                                                    <Col xs={{offset:1, span:5}} md={2}>
                                                        <Form.Control type="file" id="gallery-file"></Form.Control>
                                                    </Col>
                                                    <Col xs={{offset:1, span:5}} md={2}>
                                                        <Form.Label id="gallery-filename" style={{border:"solid 1px blue"}}></Form.Label>
                                                    </Col>
                                                    <Col xs={{offset:1, span:10}} md={7}>
                                                        <Form.Control type="text" id="gallery-caption" placeholder="Caption"></Form.Control>
                                                    </Col>
                                                    <Col  md={1}>
                                                        <Button variant="primary" value="gallery-upload">
                                                            Upload
                                                        </Button>
                                                    </Col>
                                                </Form.Row>
                                            </Form>
                                        </Row>
                                    {/* </Container> */}
                                </div>
                            </Tab>
                            <Tab eventKey="carousel" title="CAROUSEL">
                                Carousel
                            </Tab>
                        </Tabs>
                    </div>
                <Footer/>
            </Container>
        )
    }
}

export default ModeratorDashboard;
