import React from 'react';
import ReactDOM from 'react-dom';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import { Card, CardImg, CardText, CardBody, CardTitle, CardSubtitle, CardLink } from 'reactstrap';
import Button from 'react-bootstrap/Button';
import MaterialIcons from 'material-icons-react';
import Figure from 'react-bootstrap/Figure';
import Header from './Components/Header';
import Footer from './Components/Footer';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Table from 'react-bootstrap/Table';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab'
import Sweetalert from 'react-bootstrap-sweetalert';
import ModeratorSingleJob from './ModeratorSingleJob';

class ModeratorJobHandle extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      jobListArray: [],
      arrayLength: 0
    }
  }
  componentDidMount(){
    let _self_parent = this;
    axios({
      method: 'GET',
      url: '/jobs'
    }).then((response) => {
      console.log(response);
      if(response.data.status === "success"){
        _self_parent.setState({jobListArray: response.data.data, arrayLength: response.data.data.length});
      }
    }).catch((error) => {
      console.log(error);
    })
  }
  render() {
    let elem = "";
    if(this.state.arrayLength){
      elem = this.state.jobListArray.map((item) => {
          return(<SingleJobDisplay
            key = {item._id}
            jodId = {item._id}
            jobTitle = {item.jobTitle}
            jobDescription = {item.jobDescription}
            jobApplicationLink = {item.jobApplicationLink}
            jobDeadline = {item.jobDeadline}
            jobCompanyName = {item.jobCompanyName}
            jobLocation = {item.jobLocation}
            jobDetails = {item}
            />);
      })
    }
    return(
      <Container>
        <Header/>
            <Container style = {{marginTop: "20vh", padding: 0}}>
              <Card width = "10">
                <CardBody>
                  <CardTitle><CardLink href = "#">Job Title</CardLink></CardTitle>
                  <CardText>Job Description</CardText>
                  <CardText>Company Name</CardText>
                  <CardText>Deadline</CardText>
                  <CardText>Location</CardText>
                  <CardText>Posted by</CardText>
                    <div>
                        <Button className="float-left">Edit</Button>&nbsp;
                        <Button>Delete</Button>
                    </div>
                </CardBody>
              </Card>
            </Container>
      </Container>
    )
  }
}
export default ModeratorJobHandle;
