    render() {
        const formStyle = {
            marginTop: "5vh"
        }
        const formLabelStyle = {
            color: "#ffffff"
        }
        const centreText = {
            margin: "0.5em auto 0em",
            color: "white"
        }
        const dummy = process.env.PUBLIC_URL + "images/dummy.png";
        const profile_pic_style = {
            width: "100%",
            height: "auto"
        };
        return (
            <Container>
                <Header />
                <Row style={{marginTop: "10em"}}>
                    <Col xs={12} md={{ span: 6, offset: 3 }}>
                        <Row>
                            <Col xs={{span: 8, offset: 2}} md={{span: 6, offset: 3}} >
                                <Image src={dummy} alt="productImg" style={profile_pic_style} thumbnail />
                            </Col>
                        </Row>
                        <Row>
                            <span style={centreText}>Email : ajit456behera@gmail.com</span>
                        </Row>
                    </Col>
                </Row>
                <Form style={formStyle}>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfileName">
                                <Form.Label style={formLabelStyle}>Full Name</Form.Label>
                                <Form.Control type="text" placeholder="Full Name" onChange={this.handleChangeFullName} />
                                {/* <Form.Text className="text-muted">
                                    We'll never share your email with anyone else.
                                </Form.Text> */}
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfileName">
                                <Form.Label style={formLabelStyle}>Date of Birth</Form.Label>
                                <Form.Control type="date" onChange={this.handleChangeDOB} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfilePreAddress">
                                <Form.Label style={formLabelStyle}>Present Address</Form.Label>
                                <Form.Control type="text" placeholder="Present Address" onChange={this.handleChangePreAdr} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfilePerAddress">
                                <Form.Label style={formLabelStyle}>Permanent Address</Form.Label>
                                <Form.Control type="text" placeholder="Permanent Address" onChange={this.handleChangePerAdr} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfilePhone">
                                <Form.Label style={formLabelStyle}>Mobile Number</Form.Label>
                                <Form.Control type="text" placeholder="Phone Number" onChange={this.handleChangeMob} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfileFather">
                                <Form.Label style={formLabelStyle}>Father's Name</Form.Label>
                                <Form.Control type="text" placeholder="Father's name in full" onChange={this.handleChangeFather} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfileMother">
                                <Form.Label style={formLabelStyle}>Mother's Name</Form.Label>
                                <Form.Control type="text" placeholder="Mother's name in full" onChange={this.handleChangeMother} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfileSpouse">
                                <Form.Label style={formLabelStyle}>Spouse's Name</Form.Label>
                                <Form.Control type="text" placeholder="Spouse's name in full" onChange={this.handleChangeSpouse} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfileChildren">
                                <Form.Label style={formLabelStyle}>Children</Form.Label>
                                <Form.Control type="text" placeholder="For multiple values separate using ' , '" onChange={this.handleChangeChildren} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfileChildren">
                                <Form.Label style={formLabelStyle}>Siblings</Form.Label>
                                <Form.Control type="text" placeholder="For multiple values separate using ' , '" onChange={this.handleChangeSiblings} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Group controlId="ProfileChildren">
                                <Form.Label style={formLabelStyle}>Hobbies</Form.Label>
                                <Form.Control type="text" placeholder="For multiple values separate using ' , '" onChange={this.handleChangeHobbies} />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Row>
                                <Col xs={2}>
                                    <Button variant="primary" value="login" onClick={this.handleLogin}>
                                        Submit
                                    </Button>
                                </Col>
                                <Col xs={{ span: 2, offset: 2 }}>
                                    <Button variant="primary" value="recover" onClick={this.handleLogin}>
                                        Recover
                                    </Button>
                                </Col>
                                <Col xs={{ span: 2, offset: 2 }}>
                                    <Button variant="primary" value="register" onClick={this.handleLogin}>
                                        Register
                                    </Button>
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                    <Row style={{ paddingTop: 20 }}>
                        <Col xs={12} md={{ span: 6, offset: 3 }}>
                            <Form.Label style={formLabelStyle}>One click login with</Form.Label>
                            <Row>
                                <Col>
                                    <SocialIcon url="http://localhost:3001/auth/facebook" network="facebook" bgColor="#ffffff" />
                                </Col>
                                <Col>
                                    <SocialIcon url="http://localhost:3001/auth/linkedin" network="linkedin" bgColor="#ffffff" />
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                </Form>
                {/* <Sweetalert danger confirmBtnText="I Understand" confirmBtnBsStyle="primary" title="Authentication Error" show={this.state.loginAlertShow} onConfirm={this.handleLoginAlertClose}>
                    {this.state.loginAlertText}
                </Sweetalert> */}
                <Footer />
            </Container>
        )
    }
}

export default Profile;
